import "./Update.scss";
import { makeRequest } from "../../axios";
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Mutation } from "@tanstack/react-query";

    const Update = ({ setOpenUpdate , user}) => {

        const [Cover, setCover] = useState(null)
        const [Profile, setProfile] = useState(null)
        const [texts, setTexts] = useState({
            name: "",
            city: "",
            school: "",
        });

        const upload = async (file) => {
            try {
              const formData = new FormData();
              formData.append("file", file);
              const res = await makeRequest.post("/upload", formData);
              return res.data;
            } catch (err) {
              console.log(err);
            }
          };

          const queryClient = useQueryClient();

          //Code taken from: https://www.npmjs.com/package/multer
          const mutation = useMutation(
            (user) => {
              return makeRequest.put("/users",(user));
            },
            {
              onSuccess: () => {
                queryClient.invalidateQueries(["user"]);
              },
            }
          );

          const handleClick = async (e) => {
            e.preventDefault();
            let coverUrl = user.coverPic;
            let profileUrl = user.profilePic;

            coverUrl = cover && await upload(Cover)
            profileUrl = profile && await upload(Profile)
            if (file) imgUrl = await upload();

            mutation.mutate({ ...texts, coverPic:coverUrl, profilePic:profileUrl });
            setOpenUpdate(false);
          };
        


    const handleChange = (e) => {
        setTexts((prev) => ({ ...prev, [e.targetname]: [e.target.value]}))
    };

    return (
        <div className="update">Update
        <form>
            <input type="file" />
            <input type="file" />
            <input type="text" name="name" onChange={handleChange}/>
            <input type="text" name="city" onChange={handleChange}/>
            <input type="text" name="school" onChange={handleChange}/>
            <button onClick={handleClick}>Update</button>
        </form>
        <button onClick={()=>setOpenUpdate(false)}>X</button>
        </div>
    )
 } 

export default Update;
