import "./Post.scss";
import FavoriteBorderOutlinedIcon from "@mui/icons-material/FavoriteBorderOutlined";
import FavoriteOutlinedIcon from "@mui/icons-material/FavoriteOutlined";
import TextsmsOutlinedIcon from "@mui/icons-material/TextsmsOutlined";
import ShareOutlinedIcon from "@mui/icons-material/ShareOutlined";
import MoreHorizIcon from "@mui/icons-material/MoreHoriz";
import { Link } from "react-router-dom";
// import Comments from "../comments/Comments";
import { useState, useContext } from "react";
import Comments from "../Comments/Comments";
import moment from "moment";
import { AuthContext } from "../../context/authContext";
import {makeRequest} from "../../axios";
import { useQuery } from "@tanstack/react-query";


const Post = ({ post }) => { // change Post to post
  //Function for opening the comment section
  const [commentOpen, setCommentOpen] = useState(false);

  const {currentUser} = useContext(AuthContext)

  const { isLoading, data } = useQuery(["likes", post.id], () =>
  makeRequest.get("/likes?postId=" + post.id).then((res) => {
    return res.data;
  })
);

  

  return (
    <div className="Post">
      <div className="container">
        <div className="user">
          <div className="userInfo">
            <img src={post.profilePic} alt="" />
            <div className="details">
              <Link
                to={`/profile/${post.userId}`}
                style={{ textDecoration: "none", color: "inherit" }}
              >
                <span className="name">{post.name}</span>
              </Link>
              <span className="date">{moment(post.createdAt).fromNow()}</span>
            </div>
          </div>
          <MoreHorizIcon />
        </div>
        <div className="content">
          <p>{post.desc}</p>
          <img src={"./upload/"+post.img} alt="" />
        </div>
        <div className="info">
          <div className="item">
            {isLoading ? "loading" : data.includes(currentUser.id) ? <FavoriteOutlinedIcon style={{color:"red"}}onClick={handleLike} /> : <FavoriteBorderOutlinedIcon onClick={handleLike} />}
            {data?.length} Likes
          </div>
          {/* Makes it so that when you click on the icon, it opens the comments */}
          <div className="item" onClick={() => setCommentOpen(!commentOpen)}>
            <TextsmsOutlinedIcon />
            Comments
          </div>
          <div className="item">
            <ShareOutlinedIcon />
            Share
          </div>
        </div>
        {/* This is the condition */}
        {commentOpen && <Comments postId={post.id}/>}
      </div>
    </div>
  );
};

export default Post;
