import "./Profile.scss";
import FacebookTwoToneIcon from "@mui/icons-material/FacebookTwoTone";
import LinkedInIcon from "@mui/icons-material/LinkedIn";
import InstagramIcon from "@mui/icons-material/Instagram";
import PinterestIcon from "@mui/icons-material/Pinterest";
import TwitterIcon from "@mui/icons-material/Twitter";
import PlaceIcon from "@mui/icons-material/Place";
import EmailOutlinedIcon from "@mui/icons-material/EmailOutlined";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import SchoolIcon from '@mui/icons-material/School';
import Posts from "../../components/Posts/Posts";
import {makeRequest} from "../../axios";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "react-router-dom";
import "./Profile.scss"
import { useContext } from "react";
import { AuthContext } from "../../context/authContext";

const Profile = () => {

    const {currentUser} = useContext(AuthContext)

    const userId = parseInt(useLocation().pathname.split("/")[2]);

    const { isLoading, error, data } = useQuery(["user"], () =>
    makeRequest.get("/users/find/" + userId).then((res) => {
      return res.data;
    })
  );

  const { risLoading, data: relationshipData } = useQuery(["user"], () =>
    makeRequest.get("/relationships?followedId=" + userId).then((res) => {
      return res.data;
    })
  );

  console.log(relationshipData)


  const handleFollow = () => {
    
  }

    return (
        <div className="profile">
            {isLoading ? ("loading") : (
                 <><div className="images">
                <img src={data.coverPic} alt="" className="coverphoto" />
                <img src={data.profilePic} alt="" className="profilephoto" />
            </div>
            <div className="profileinfo">
                <div className="userinfo">
                    <div className="left">
                        <a href="http://facebook.com">
                            <FacebookTwoToneIcon fontSize="large" />
                        </a>
                        <a href="https://www.instagram.com">
                        <   InstagramIcon fontSize="large" />
                        </a>
                        <a href="https://www.instagram.com">
                            <TwitterIcon fontSize="large" />
                        </a>
                        <a href="https://www.linkedin.com">
                            <LinkedInIcon fontSize="large" />
                        </a>
                        <a href="https://www.pinterest.com">
                            <PinterestIcon fontSize="large" />
                        </a>
                    </div>
                    <div className="center">
                        <span>{data.name}</span>
                            <div className="info">
                        <div className="item">
                            <PlaceIcon />
                        <span>{data.city} </span>
                        </div>
                        <div className="item">
                            <SchoolIcon />
                        <span>{data.school}</span>
                        </div>
                            </div>
                           { risLoading ? "loading" :  userId === currentUser.id ? (<button>Update</button>) : <button onClick={handleFollow}>{relationshipData.include(currentUser.id) ? "Following" : "Follow"}</button>
                        }
                    </div>
                    <div className="right">
                    <EmailOutlinedIcon />
                    <MoreVertIcon />
                    </div>
                </div>
                <Posts />
            </div>
            </>
            )}
        </div>      
    )
}

export default Profile
